import React, { Fragment ,useState} from 'react'

function Registration() {

    const [inputs,setInputs ]= useState({
        name : "",
        email : "",
        password : ""
    })

    const {email,name,password} =inputs
    const onChange =(e)=>{
        setInputs({...inputs,[e.target.name]  : e.target.values })
    }
    return (
        <Fragment>
        <h1 className="text-centre">Registration</h1>
        <form>
            <input type="email" name="email"  placeholder="email"  className="form-control my-3"  value={email} onChange={e=>onChange(e)}/>
            <input type="password" name="password"  placeholder="password"  className="form-control my-3"  value={pass}  onChange={e=>onChange(e)}/>
            <input type="text" name="name"  placeholder="name"  className="form-control my-3" value={email}  onChange={e=>onChange(e)}/>
            <button className="btn btn-success btn-block">Register</button>
        </form>
    </Fragment>
    )
}

export default Registration
