import { Fragment,useState ,useEffect} from 'react';
import './App.css';
import  {BrowserRouter as Router , Switch,Route,Redirect} from "react-router-dom"
import Login from './components/Login__Registration/Login';
import Registration from './components/Login__Registration/Registration';
import Dashboard from "./components/Login__Registration/Dashboard/Dashboard"

function App() {

  const [isAuthenticated,setIsAuthenticated]=useState(false);

  use

  const setAuth =(boolean)=>{
    setIsAuthenticated(boolean)
  }
  return (
    <Fragment>
      <Router>
        <div className="container">
          <Switch>
              <Route exact path="/login" render={props => !isAuthenticated ?  (<Login  {...props} setAuth={setAuth} />)  :  (<Redirect to="/dashboard"/>)  }/>
              <Route exact path="/register" render={props => !isAuthenticated ?  (<Registration  {...props}  setAuth={setAuth} />)  : (<Redirect to="/login"/>)  }  />
              <Route exact path="/dashboard" render={props => isAuthenticated ?  <Dashboard  {...props} setAuth={setAuth} />  :  (<Redirect  to="/login" />) }/>
          </Switch>
        </div>
      </Router>

    </Fragment>
  );
}

export default App;
