import { Fragment,useState } from 'react';
import './App.css';
import  {BrowserRouter as Router , Switch,Route,Redirect} from "react-router-dom"
import Dashboard from "./components/Login__Registration/Dashboard/Dashboard"
import Login from './components/Login__Registration/Login';
import Registration from './components/Login__Registration/Registration';

function App() {

  const [isAuthenticated,setIsAuthenticated]=useState(false);

  return (
    <Fragment>
      <Router>
        <div className="container">
          <Switch>
              <Route exact path="/login" render={props => !isAuthenticated ?  (<Login  {...props}/>)  :  (<Redirect to="/dashboard"/>)  }/>
              <Route exact path="/register" render={props => !isAuthenticated ?  (<Registration  {...props}/>)  : (<Redirect to="/login"/>)  }  />
              <Route exact path="/dashboard" render={props => <Dashboard  {...props}/>}  />
          </Switch>
        </div>
      </Router>

    </Fragment>
  );
}

export default App;
